mapper.select:
SELECT CONCAT_WS( '-', temp.warehouse_code, temp.customer_code, temp.snapshot_time ) AS bs_code, temp.warehouse_code, temp.customer_id, temp.customer_code, temp.sku_id, temp.length, temp.width, temp.height,
SUM( CONVERT( IF( temp.min_charge_volume > temp.volume * 0.000001, temp.min_charge_volume, temp.volume * 0.000001),
) * temp.warehouse_stock ) AS itm_volume,
SUM( CONVERT( temp.weight,
) ) * temp.warehouse_stock AS itm_weight, SUM(temp.warehouse_stock) AS itm_quantity, temp.stock_age, temp.charge_day_num AS charge_day_num, QUARTER (str_to_date(#{criteria.toSnapshotTime}, '%Y%m%d')) AS charge_season,
'N' AS itm_sign, CURRENT_TIMESTAMP AS create_time, 0 AS charge_num, 'STANDARD' AS charge_mode, temp.snapshot_time FROM ( SELECT temp_ivs.snapshot_time, temp_ivs.warehouse_code, temp_ivs.customer_code, temp_ivs.customer_id, temp_ivs.sku_id, temp_ivs.warehouse_stock, temp_ivs.stock_age, temp_ivs.charge_day_num, temp_ivs.min_charge_volume, IFNULL(vsbi.length, 0) AS length, IFNULL(vsbi.width, 0) AS width, IFNULL(vsbi.height, 0) AS height, IFNULL(vsbi.volume, 0) AS volume, IFNULL(vsbi.weight, 0) AS weight FROM ( SELECT ivs.snapshot_time, ivs.warehouse_code, ivs.customer_code, ivs.customer_id, ivs.sku_id, ivs.warehouse_stock,
FROM inventory_snapshot ivs
) AS temp_ivs INNER JOIN v_sku_business_info vsbi ON temp_ivs.sku_id = vsbi.sku_id ) AS temp GROUP BY temp.warehouse_code, temp.customer_id, temp.customer_code, temp.sku_id, temp.stock_age

